// src/App.js
import React, { useState } from 'react';
import './App.css';
import AuthPage from './Authentication/auth_page';
import ExpenseTracker from './expense/expense_tracker';
import Sidebar from './expense/sidebar';
import Budget from './Dashboard/budget';
import Analytics from './Dashboard/analytics';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [currentPage, setCurrentPage] = useState('dashboard');


  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleNavigation = (page) => {
    setCurrentPage(page);
  };


  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  return (
    <div className="app">
      {!isLoggedIn ? (
        <AuthPage onLogin={handleLogin} />
      ) : (
        <><Sidebar onNavigate={handleNavigation} /><div className="main-content">
            {currentPage === 'dashboard' && <ExpenseTracker username={username} />}
            {currentPage === 'budget' && <Budget />}
            {currentPage === 'analytics' && <Analytics />}
          </div><ExpenseTracker username={username} onLogout={handleLogout} /></>
      )}
    </div>
  );
};

export default App;
