// src/Bills.js
import React, { useState } from 'react';

const Bills = () => {
  const [bills, setBills] = useState([]);
  const [billName, setBillName] = useState('');
  const [billAmount, setBillAmount] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [isCreditCardBill, setIsCreditCardBill] = useState(false);
  const [cdNumber, setcdNumber] = useState('');

  const handleAddBill = (e) => {
    e.preventDefault();
    const newBill = { billName, billAmount, dueDate, isCreditCardBill, cdNumber: isCreditCardBill ? cdNumber : '' };
    setBills([...bills, newBill]);
    setBillName('');
    setBillAmount('');
    setDueDate('');
    setIsCreditCardBill(false);
    setcdNumber('');
  };

  // const handleRemoveBill = (index) => {
  //   setBills(bills.filter((_, i) => i !== index));
  // };

  return (
    <div>
      <h2>Manage Bills</h2>
      <form onSubmit={handleAddBill}>
        <div>
          <label>Bill Name:</label>
          <input
            type="text"
            value={billName}
            onChange={(e) => setBillName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Bill Amount:</label>
          <input
            type="number"
            value={billAmount}
            onChange={(e) => setBillAmount(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Due Date:</label>
          <input
            type="date"
            value={dueDate}
            onChange={(e) => setDueDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>
            <input
              type="checkbox"
              checked={isCreditCardBill}
              onChange={(e) => setIsCreditCardBill(e.target.checked)}
            />
            Is Credit Card Bill
          </label>
        </div>
        {isCreditCardBill && (
          <div>
            <label>Card Number:</label>
            <input
              type="text"
              value={cdNumber}
              onChange={(e) => setcdNumber(e.target.value)}
            />
          </div>
        )}
        <button type="submit">Add Bill</button>
      </form>
    </div>
  );
};

export default Bills;
