import React, {useState} from 'react';
import './dashboard.css';
import ExpenseTracker from '../expense/expense_tracker';
import PieChartComponent from './Dashboard/piechart';
import TotalBudget from './Dashboard/total_budget';
import UpcomingBills from './Dashboard/upcoming_bills';

const Dashboard = ({ }) => {
    const [expenses, setExpenses] = useState([
        { amount: 12, description: 'Dinner', category: 'Food/Beverage' },
        { amount: 240, description: 'Cafe', category: 'Travel/Commute' },
        { amount: 240, description: 'Groceries', category: 'Food/Beverage' },
      ]);
    
      const totalBudget = 1000;
    
      const handleAddExpense = (expense) => {
        setExpenses([...expenses, expense]);
      };
    
      const pieData = expenses.reduce((acc, expense) => {
        const existingCategory = acc.find((item) => item.name === expense.category);
        if (existingCategory) {
          existingCategory.value += expense.amount;
        } else {
          acc.push({ name: expense.category, value: expense.amount });
        }
        return acc;
      }, []);
    
      const remainingBudget = totalBudget - expenses.reduce((acc, expense) => acc + expense.amount, 0);
    
      const bills = [
        { name: 'Electricity', amount: 100, dueDate: '2024-07-01' },
        { name: 'Internet', amount: 50, dueDate: '2024-07-05' },
      ];
    

    return (
        <div className="dashboard">
            <div className="dashboard-container">
                <div className="dashboard-grid">
                    <div className="dashboard-item">
                        <ExpenseTracker/>
                    </div>
                    <div className="dashboard-item">
                        <PieChartComponent data={pieData} />
                    </div>
                    <div className="dashboard-item">
                        <TotalBudget totalBudget={totalBudget} remainingBudget={remainingBudget} />
                    </div>
                    <div className="dashboard-item">
                        <UpcomingBills bills={bills} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Dashboard;
