import React, { useState } from 'react';
import './App.css';
import LoginPage from './Authentication/auth_page';
import SignupPage from './Authentication/signup_page';
import ExpenseTracker from './expense/expense_tracker';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  return (
    <div className="app">
      {!isLoggedIn ? (
        <>
          <LoginPage onLogin={handleLogin} />
          <SignupPage />
        </>
      ) : (
        <ExpenseTracker username={username} onLogout={handleLogout} />
      )}
    </div>
  );
};

export default App;
