// src/App.js
import React, { useState } from 'react';
import './App.css';
import AuthPage from './Authentication/auth_page';
import ExpenseTracker from './expense/expense_tracker';
import Budget from './Dashboard/budget';
import Analytics from './Dashboard/analytics';
import Sidebar from './expense/sidebar';
import Dashboard from './Dashboard/dashboard'

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [currentPage, setCurrentPage] = useState('dashboard');


  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  // const handleNavigation = (page) => {
  //   setCurrentPage(page);
  // };


  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  return (
    <div className="app">
      {!isLoggedIn ? (
        <div className="app-login">
          <AuthPage onLogin={handleLogin} />
        </div>
      ) : (
        // <div className="app-container">
        //     <Sidebar onNavigate={handleNavigation} onLogout={handleLogout} />
        //     <div className="main-content">
        //       {currentPage === 'dashboard' && <ExpenseTracker username={username} />}
        //       {currentPage === 'budget' && <Budget />}
        //       {currentPage === 'analytics' && <Analytics />}
        //     </div>
        // </div>
        <Dashboard username={username} onLogout={handleLogout} />
      )}
    </div>
  );
};

export default App;
