// src/App.js
import React, { useState } from 'react';
import './App.css';
import AuthPage from './Authentication/auth_page';
import ExpenseTracker from './expense/expense_tracker';
import Budget from './Sidebar/budget';
import Analytics from './Sidebar/analytics';
import Sidebar from './Sidebar/sidebar';
import Dashboard from './Sidebar/dashboard'
import UpcomingBills from './Sidebar/Dashboard/upcoming_bills';
import PieChartComponent from './Sidebar/Dashboard/piechart';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [currentPage, setCurrentPage] = useState('dashboard');


  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleNavigation = (page) => {
    setCurrentPage(page);
  };


  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
  };

  const handleNavigate = (page) => {
    setCurrentPage(page);
  };


  return (
    <div className="app">
      {!isLoggedIn ? (
        <div className="app-login">
          <AuthPage onLogin={handleLogin} />
        </div>
      ) : (
        <div className="app-container">
            <Sidebar onNavigate={handleNavigation} username={username} onLogout={handleLogout} />
            <div className="main-content">
              {currentPage === 'dashboard' && <Dashboard onNavigate={handleNavigate} />}
              {currentPage === 'expense_tracker' && <ExpenseTracker />}
              {currentPage === 'budget' && <Budget />}
              {currentPage === 'analytics' && <Analytics />}
              {currentPage === 'analytics' && <PieChartComponent/>}
              {currentPage === 'upcoming-bills' && <UpcomingBills />}
            </div>
        </div>
      )}
    </div>
  );
};

export default App;
